import React, { Component } from 'react'
import './index.css'

export default class Item extends Component {
    state = {mouse:false}

    handleMouse = (flag) =>{

        return (event)=>{
            //console.log(flag)
            this.setState({mouse:flag})                
        }
    }

    handleCheck = (id) => {
        //方式1: 高阶函数
        return (event) =>{
            console.log(id, event.target.checked)

            //执行从porps传入的回调函数 --定义在App组件中
            this.props.changeTodo(id)
        }
    }

    //删除一个TODO
    handleDelete =(id) => {
        //方式2 -- 直接执行， 但是事件监听处，需要包装成函数
        //执行从porps传入的回调函数 --定义在App组件中
        this.props.deleteTodo(id)
    }
    render() {
        const { id, name, done} = this.props
        const {mouse} = this.state
        return (
            <li style={{backgroundColor: mouse ? '#ddd': '#fff'}}
                onMouseEnter={this.handleMouse(true)}
                onMouseOut={this.handleMouse(false)}>
                <label>
                    <input type="checkbox" defaultChecked={done} onChange={this.handleCheck(id)}/>
                    <span>{name}</span>
                </label>
                <button onClick={ () => {this.handleDelete(id)}} 
                className="btn btn-danger" 
                style={{ display: mouse?'block': 'block' }}>删除</button>
            </li>
        )
    }
}
